import React from 'react'
import { renderIntoDocument } from 'react-dom/test-utils';

const HOC =(WrappedComponent,entity) =>{
    return class extends React.Component{
        state={
            data:[],
            term:""
        };
        componentDidMount(){
            const fetchdata= async () =>{
                const res = await fetch(`https://jsonplaceholder.typicode.com/${entity}`);
                const json=  await res.json()
                this.setState({...this.state,data:json})
        }
        fetchdata();
    } 
        render(){
            let {data,term} =this.state;
            let filtereddata= data.filter((d)=>{
                if(entity ==="users"){
                    const{name} =d;
                    return name.indexOf(term) >=0;
                }
                if(entity=== "todos"){
                    const{title}=d;
                    return title.indexOf(term) >=0;
                }
            })
            return(
                <div>
                <h2>{entity}</h2>
                <input type="text" value={term} onChange={(e) =>
                    this.setState({...this.state, term:e.target.value})}></input>
                   <WrappedComponent data={filtereddata}>

                   </WrappedComponent>


                </div>



            )

        }
    }
} 
export default HOC
